package core;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Random;
import java.util.UUID;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.web.bind.annotation.CrossOrigin;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RestController;

import model.Material;
import model.Product;
import model.ProductCategory;
import model.User;

@SpringBootApplication
@CrossOrigin(maxAge = 4800, allowCredentials = "false") 
@RestController
@RequestMapping("/api")
public class RestApp {

	private static Logger logger = LoggerFactory.getLogger(RestApp.class);

	@RequestMapping(value="/resource",method = RequestMethod.GET)
	public Map<String,Object> home() {
		Map<String,Object> model = new HashMap<String,Object>();
		model.put("id", UUID.randomUUID().toString());
		model.put("content", "Hello World");
		return model;
	}

	//Product section
	@RequestMapping(value="/product/get/{productCatId}",method = RequestMethod.GET)
	public List<Product> getProducts(@PathVariable("productCatId")Integer productCatId) {

		logger.info("Get product of Product Category ID");

		ProductCategory c = new ProductCategory();
		c.setId(productCatId);
		c.setName("Production Cat");

		List<Product> list = new ArrayList<Product>();


		for(int i=1;i<10;i++){

			Product p = new Product();
			p.setId(i);
			p.setBaseQuantity(1000);

			Material m = new Material();
			m.setId(i);
			m.setName("Material" + i);

			p.setMaterial(m);
			p.setHeight(100);
			p.setWidth(100);
			p.setName("Product" + i);
			p.setUnit("Unit");
			p.setPrice(new Double(1000*i));

			list.add(p);
		}


		return list;
	}

	@RequestMapping(value="/product/add/",method = RequestMethod.PUT)
	public Boolean addProduct(@RequestBody Product product) {

		logger.info("Add new product");

		Random rand = new Random();

		if(rand.nextInt(1)==0){
			return false;
		}

		return true;
	}

	@RequestMapping(value="/product/update/",method = RequestMethod.PUT)
	public Boolean updateProduct(@RequestBody Product product) {

		logger.info("Update product with id = " + product.getId());

		Random rand = new Random();

		if(rand.nextInt(1)==0){
			return false;
		}

		return true;
	}

	@RequestMapping(value="/product/delete/{productId}",method = RequestMethod.DELETE)
	public Boolean deleteProduct(@PathVariable("productId")Integer productId) {

		logger.info("Delete product with id = " + productId);

		Random rand = new Random();

		if(rand.nextInt(1)==0){
			return false;
		}

		return true;
	}



	@RequestMapping(value="/product/search/{name}",method = RequestMethod.GET)
	public List<Product> searchProducts(@PathVariable("name")String name) {

		logger.info("Search products by name");

		ProductCategory c = new ProductCategory();
		c.setId(1);
		c.setName("productCat1");

		List<Product> list = new ArrayList<Product>();


		for(int i=1;i<10;i++){

			Product p = new Product();
			p.setId(i);
			p.setBaseQuantity(1000);

			Material m = new Material();
			m.setId(i);
			m.setName("Material" + i);

			p.setMaterial(m);
			p.setHeight(100);
			p.setWidth(100);
			p.setName("Product" + i);
			p.setUnit("Unit");
			p.setPrice(new Double(1000*i));

			p.setProductCategory(c);
			
			list.add(p);
		}


		return list;
	}

	//Product Category section
	@RequestMapping(value="/productCategory/get/all",method = RequestMethod.GET)
	public List<ProductCategory> getAllProductCategory() {

		logger.info("Get all product categories");

		List<ProductCategory> list = new ArrayList<ProductCategory>();

		for(int i=1;i<10;i++){

			ProductCategory pc = new ProductCategory();
			pc.setId(i);
			pc.setName("productCat" + i);
			pc.setImage("data:image/jpeg;base64,");

			list.add(pc);
		}

		return list;
	}


	@RequestMapping(value="/productCategory/get/{productCatId}",method = RequestMethod.GET)
	public ProductCategory getProductCategory(@PathVariable("productCatId")Integer productCatId) {

		logger.info("Get product of Product Category ID");

		ProductCategory c = new ProductCategory();
		c.setId(productCatId);
		c.setName("Production Cat");

		return c;
	}

	@RequestMapping(value="/productCategory/add/",method = RequestMethod.PUT)
	public Boolean addProductCategory(@RequestBody ProductCategory productCat) {

		logger.info("Add new product");

		Random rand = new Random();

		if(rand.nextInt(1)==0){
			return false;
		}

		return true;
	}

	@RequestMapping(value="/productCategory/update/",method = RequestMethod.PUT)
	public Boolean updateProductCategory(@RequestBody ProductCategory productCat) {

		logger.info("Update productCategory with id = " + productCat.getId());

		Random rand = new Random();

		if(rand.nextInt(1)==0){
			return false;
		}

		return true;
	}

	@RequestMapping(value="/productCategroy/delete/{productCatId}",method = RequestMethod.DELETE)
	public Boolean deleteProductCategory(@PathVariable("productCatId")Integer productCatId) {

		logger.info("Delete product category with id = " + productCatId);

		Random rand = new Random();

		if(rand.nextInt(1)==0){
			return false;
		}

		return true;
	}



	@RequestMapping(value="/productCategory/search/{name}",method = RequestMethod.GET)
	public List<ProductCategory> searchProductCategories(@PathVariable("name")String name) {

		logger.info("Search product category by name");

		ProductCategory c = new ProductCategory();
		c.setId(1);
		c.setName("Production Cat");

		List<ProductCategory> list = new ArrayList<ProductCategory>();

		list.add(c);

		return list;
	}

	//Material section
	@RequestMapping(value="/material/get/all",method = RequestMethod.GET)
	public List<Material> getAllMaterial() {

		logger.info("Get all materials");

		List<Material> list = new ArrayList<Material>();

		for(int i=1;i<10;i++){

			Material m = new Material();
			m.setId(i);
			m.setName("material" + i);

			list.add(m);
		}

		return list;
	}



	//login
	@RequestMapping(value="/login/{username}/{pwd}",method = {RequestMethod.OPTIONS,RequestMethod.GET})
	public User login(@PathVariable("username")String username, @PathVariable("pwd")String pwd) {

		logger.info("login");

		if(username.equals("test")&&pwd.equals("1234")){
			return new User("1", "test", "test@gmail.com", "1234", "customer");
		}
		return null;
	}

	public static void main(String[] args) {
		SpringApplication.run(RestApp.class, args);
	}

}
